function ShowPlan() {
  const [planes, setPlanes] = useState(initialPlanes)
  const { currentUser } = useAuth();
  const [count, setCount] = useState(12500);

  const handleChangePlan = useCallback((updatePlan) => {
    const newPlanes = [...planes]
    const indexPlan = newPlanes.findIndex((item) => item.name === updatePlan.name)
    newPlanes.splice(indexPlan, 1, updatePlan)
    setPlanes(newPlanes)
  }, [planes])

  function increment() {
    if (count < 16500) {
      setCount(count + 100)
    }
  }

  function decrement() {
    if (count > 12500) {
      setCount(count - 100)
    }
  }


  return (
    <>
      <ShowData user={currentUser} />
      <Container>
        <Count increment={increment} decrement={decrement} count={count} />
        <Title>Agrega o quita coberturas</Title>
        <ShowHidenPlan count={count} planes={planes} onChangePlan={handleChangePlan} />
      </Container>
    </>


  )
}

export default ShowPlan;